import { useState, useEffect } from 'react';

function Hello(){
    useEffect(() => {
        console.log('I\'m here!');
        return () => {
            console.log('destroyed');
        }
    })
    return (
        <h1>Hello</h1>
    )
}

function App() {
    const [showing, setShowing] = useState(false)
    const onClick = () => {
        setShowing((current) => !current);
    }
    return (
        <div>
            {showing ? <Hello/> : null}
            <button onClick={onClick}>{showing ? "Hide" : "Show"}</button>
        </div>
    );
}

export default App;
