import { useState, useEffect } from 'react';
function App() {
    const [counter, setCounter] = useState(0)
    const onClick = () => {
        //setCounter((current) => current + 1);
        setCounter(1);
        console.log(counter)
        if(counter == 1){
            console.log('HI');
        }
    }
    return (
        <div>
            <button onClick={onClick}>CLICK</button>
            <span>{counter}</span>
        </div>
    );
}

export default App;
