import styles from './style.module.css'
import {useState, useEffect} from "react";


function Clock() {
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        const id = setInterval(() => {
            setTime(new Date());
        }, 1000);
        return (() => clearInterval(id))
    }, []);
    return(
        <div>
            {time.toLocaleString()}
        </div>
    )
}
function App() {
  return (
      <div>
          <Clock/>
      </div>
  );
}

export default App;
