import { useState, useEffect } from 'react';
import './body.css'
function App() {
    const [todo,setTodo] = useState("");
    const [todos,setTodos] = useState([]);
    const imgs = [
        'url("https://c4.wallpaperflare.com/wallpaper/586/603/742/minimalism-4k-for-mac-desktop-wallpaper-preview.jpg")'
    ];
    const onChange = (e) => {
        setTodo(e.target.value);
    }
    const onSubmit = (event) => {
        event.preventDefault();
        if(todo === ""){
            return;
        }
        setTodo("");
        setTodos((prev) => [...prev,todo])
    }
    useEffect(() => {
        console.log(todos);
    },[todos])
    return (
        <div className={'main-container'} style={{backgroundImage:imgs[0]}}>
            <h1 className={'title'}>My To Dos ({todos.length})</h1>
            <form onSubmit={onSubmit}>
                <input value={todo} onChange={onChange} type="text" placeholder={"write your to do..."}/>
                <button>Add to do</button>
            </form>
            <ul>
                {todos.map( (item) => {
                    return (
                        <li key={item}>{item}</li>
                    );
                })}
            </ul>
        </div>
    );
}

export default App;
