import { useState, useEffect } from 'react';
function App() {
    const [counter, setCounter] = useState(0)
    console.log('every time loaded.');
    const onlyOnce = () => {
        console.log('only once');
    }
    useEffect(onlyOnce, []);
    const onClick = () => {
        setCounter((current) => current + 1);
    }
    return (
        <div>
            <button onClick={onClick}>CLICK</button>
            <span>{counter}</span>
        </div>
    );
}

export default App;
