import styles from './style.module.css'
import {useState, useEffect} from "react";

function Background() {
    return(
        <div className={styles.background}></div>
    )
}

function Clock() {
    const [times, setTimes] = useState([new Date().getHours(), new Date().getMinutes(), new Date().getSeconds()]);
    useEffect(() => {
        clock();
        },[])
    const clock = () => {
        setInterval(() => {
            setTimes([new Date().getHours(), new Date().getMinutes(), new Date().getSeconds()]);
            clock();
        }, 1000);

    }
    return(
        <div className={styles.clock}>
            {String(times[0]).padStart(2,'0')}:{String(times[1]).padStart(2,'0')}:{String(times[2]).padStart(2,'0')}
        </div>
    )
}
function App() {
  return (
      <div>
          <Background/>
          <Clock/>
      </div>
  );
}

export default App;
